'declare',
'  l_cnt  number;',
'  l_id number;',
'begin',
' ',
'  --before I do restore, lets make a backup of current messages (if we have messages)',
'  select',
'    count(*)',
'  into ',
'    l_cnt',
'  from ',
'    APEX_APPLICATION_TRANSLATIONS ',
'  where ',
'    application_id = :P9_APP_ID and ',
'    language_code = :P9_CODE;',
'',
'    ',
'  if l_cnt > 0 then',
'  ',
'    --perform backup in case we need this messages back',
'    insert into tm_backup ',
'      (app_id, language_code, backup_time, operation) ',
'    values ',
'      (:P9_APP_ID, :P9_CODE, sysdate, ''RESTORE'')',
'    returning',
'      id ',
'    into ',
'      l_id;',
'',
'    insert into tm_backup_messages ',
'      (fk_tm_backup, translatable_message, message_text, language_code)',
'    select',
'      l_id, translatable_message, message_text, language_code',
'    from ',
'      APEX_APPLICATION_TRANSLATIONS ',
'    where ',
'      application_id = :P9_APP_ID and ',
'      language_code = :P9_CODE;',
'  ',
' ',
'  ',
'    --backup done, lets delete',
'    for cur in (select translation_entry_id ',
'                from APEX_APPLICATION_TRANSLATIONS ',
'                where application_id = :P9_APP_ID',
'                  and language_code = :P9_CODE)',
'    loop',
'        apex_lang.delete_message(cur.translation_entry_id);',
'    end loop;  ',
'  ',
'  end if;',
'  ',
'  ',
'  --all set, RESTORE!',
'  for cur in (select ',
'                translatable_message,',
'                message_text,',
'                language_code',
'              from',
'                tm_backup_messages',
'              where',
'                fk_tm_backup = :P9_FK_TM_BACKUP) ',
'  loop',
'    apex_lang.create_message(p_application_id => :P9_APP_ID,',
'                             p_name => cur.translatable_message,',
'                             p_language => cur.language_code,',
'                             p_message_text => cur.message_text );',
'  end loop;',
'  ',
' ',
' end;'))
