'declare',
'  l_inserted number := 0;',
'  l_updated  number := 0;',
'  l_failed   number := 0; ',
'begin',
'  ',
'  APEX_COLLECTION.CREATE_OR_TRUNCATE_COLLECTION(p_collection_name => ''TM_PARSE_CSV_ERRORS'');',
'',
'  for cur in (SELECT ',
'                decode(p.translatable_message, null, ''INSERT'', ''UPDATE'') action,',
'                c.c001 message_code,',
'                c.c002 translated_text',
'              FROM ',
'                apex_collections c,',
